mapper.select:
SELECT s.id, s.scholarship_name, s.description, s.type, s.amount, s.number_of_recipients, s.payment_method, s.recruitment_start_date, s.recruitment_end_date, s.evaluation_start_date, s.interview_date, s.result_announcement_date, s.evaluation_method, s.recruitment_status, s.eligibility_condition, s.grade_restriction, s.major_restriction, s.duplicate_allowed, s.min_gpa, s.category, s.contact_person_name, s.contact_phone, s.contact_email, s.office_location, s.consultation_hours, s.created_by, s.created_at, s.updated_at,
/* === 신청 여부/상태 === */ CASE WHEN a.scholarshipNm IS NULL THEN 0 ELSE 1 END AS applied, COALESCE(UPPER(a.state), 'NONE') AS application_status
FROM scholarship s LEFT JOIN application a ON a.userNm = #{userNm} AND a.scholarshipNm = s.id ORDER BY s.created_at DESC

mapper.update:
UPDATE scholarship_criteria
SET name = #{name},
	std_point = #{stdPoint},
	weight_percent = #{weightPercent}
WHERE id = #{id}

UPDATE scholarship_notice
SET title = #{title}, content = #{content}, image_url = #{imageUrl}
WHERE id = #{id}
